sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var cod_cargo = g_form.getDisplayValue('codigo_do_cargo');
    var cod_gr_desp = g_form.getDisplayValue('codigo_do_grupo_de_despesa');
    var numeroContrato = g_form.getDisplayValue('numero_do_contrato');
    var cod_recurso = g_form.getDisplayValue('codigo_recurso');
    var cod_pbms = g_form.getDisplayValue('codigo_do_pbms');
    var currentPercentual = parseFloat(g_form.getValue('percentual'));

    var ga = new GlideAjax('SomaPercentual');
    ga.addParam('sysparm_name', 'verificaSomaPercentual');
    ga.addParam('sysparm_cod_cargo', cod_cargo);
    ga.addParam('sysparm_cod_gr_desp', cod_gr_desp);
    ga.addParam('sysparm_numero_contrato', numeroContrato);
    ga.addParam('sysparm_cod_recurso', cod_recurso);
    ga.addParam('sysparm_cod_pbms', cod_pbms);
    ga.addParam('sysparm_current_percentual', currentPercentual);  // Adiciona o percentual atual como parâmetro

    ga.getXML(processaResposta);

    function processaResposta(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        var isUpdateAllowed = response.responseXML.documentElement.getAttribute("isUpdateAllowed");

        if (isUpdateAllowed === 'false') {
            g_form.clearValue('percentual');
            g_form.addErrorMessage("A soma dos percentuais ultrapassa 100%. Troque o valor do campo.");
        } else {
            g_form.addInfoMessage('A soma dos percentuais está dentro do limite.');
        }
    }
}
